INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('1811410', '1811410', '15', '', '1', '<p>该教学方法设计提纲中包含的主要数学思想方法是数形结合；其涉及的主要知识要点是抛物线的概念、标准方程及几何性质.在该教学设计中主要是使用了分层教学法，其主要体现的是因材施教的新课程理念.</p>', '10');
