INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('1815576', '15', '90909090', '<p>教材解读通常只要抓住四个核心要素即可，请简要说明这四个核心要素.</p>', '', '2015年浙江省杭州市教师招聘《小学数学》题第22题', '0', '0');
