INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('1815576', '1815576', '15', '', '1', '<p>教材解读的四个核心要素为：整体把握教材编排的结构与特点；悟透教学内容的内涵；领悟课标要求、教学目标以及隐含的教法学法；根据学生实际再创造使用教材.</p>', '7');
