INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('1815580', '15', '90909090', '<p>简述：空间观念所包含的内容有哪些？</p>', '', '2015年浙江省杭州市教师招聘《小学数学》题第23题', '0', '0');
