INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('181648865097', '15', '90909090', '<p>请看下面的数学片段：</p><p>一位教师结束了“轴对称图形”教学的新授环节，巩固练习时出现了一个“突发事件”.</p><p>老师：同学们，平行四边形是轴对称图形吗？</p><p>同学：是！</p><p>老师（拿出一个平行四边形）：它真的是平行四边形吗？大家看，把一个平行四边形对折，折痕的两边重合了吗？</p><p>学生：没有！</p><p>老师：对！大家记住，平行四边形不是轴对称图形.</p><p>问题：</p>', '', '2015年湖北农村教师招聘考试《小学数学》题第27题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='1816488';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('1816488', '181648865097', '15', '<p>（1）请分析学生错误的原因；</p>', '1', '<p>（1）学生错误的原因可能是：新授环节教师没有讲清楚，或者讲解的过程没能让学生积极思考，所以导致对概念理解不透彻.</p>', '8');
