INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('1816488', ',1816488', '1816488', '<p>本题主要考查教师教学方法.</p><p>（1）学生错误的原因可能是：新授环节教师没有讲清楚，或者讲解的过程没能让学生积极思考，所以导致对概念理解不透彻.</p>');
