INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2258129', '199146', '15', '<p>（1）简要分析歌曲的特点。</p>', '1', '<p>（1）这是一首校园童谣，歌曲采用了F大调、3/4拍，这是一首独唱歌曲。音乐情绪优美抒情，表达了少年儿童对小蜻蜓的喜爱和美好的童真童趣。</p><p>从节奏上看，歌曲多使用二分音符、四分音符以及长时值的节奏，音乐较为舒展、优美；</p><p>从旋律上看，歌曲出现了多次四度、五度的音程跳跃，塑造了活泼、阳光的音乐形象。</p>', '10');
