INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('199147', '15', '41414141', '<p>请认真阅读下列材料，并按要求作答。</p><p align="center"><img width="399px" height="165px" src="https://fb.fbstatic.cn/api/tarzan/images/166d8b257fd7d1a.png?width=700" /></p><p>       操作方法：两脚自然左右开立与肩同宽，上体稍前倾，两腿屈膝，两臂后举；两臂自然前后预摆，同时两腿随着协调弹性屈伸；起跳时两臂由后向前上方有力摆动，两脚迅速蹬地，充分蹬伸髋、膝、踝关节向前上方跳起，腾空后挺胸、展体、伸髋，使身体尽量伸展；落地前收腹、屈膝、小腿尽量前伸；落地时脚跟先着地，并立刻屈膝缓冲，保持身体平衡。</p><p>请根据上述材料完成下列任务：</p>', '', '2018年下半年教师资格证考试《教育教学知识与能力》（小学）题第30题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2258130';
