INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('199148', '15', '41414141', '<p>请认真阅读下列材料，并按要求作答。</p><p><img width="539px" height="764px" src="https://fb.fbstatic.cn/api/tarzan/images/166f1729f6e74b7.png?width=700" /></p><p><img width="539px" height="764px" src="https://fb.fbstatic.cn/api/tarzan/images/166f172bcb19265.png?width=700" /></p><p>请根据上述材料完成下列任务：</p>', '', '2018年下半年教师资格证考试《教育教学知识与能力》（小学）题第31题', '1', '0');
