INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2258131', '199148', '15', '<p>（1）什么是剪纸的装饰纹样？请列出6种剪纸装饰纹样？</p>', '1', '<p>（1）剪纸的装饰纹样是许多民间剪纸艺人在长期的剪纸实践中总结出来的，用于表现特定事物，美化剪纸块面的装饰纹样。</p><p>纹样：锯齿纹、圆形纹、鱼鳞纹、月牙纹、龙纹、云纹、漩涡纹等。</p>', '10');
