INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2026722823315', ',2026722823315', '2026722823315', '<p>（3）第一，在论述“如果对待张烨这样的学生的时候”要结合教育教学知识。第二，要加入适当的阐述。</p>');
