INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('185098', '15', '70707070', '<p>小张不爱说话，不与同学交流，不主动发言，受到表扬不喜形于色，受到指责批评大哭，用铅笔戳腿，经调查发现父亲经常外出出差，母亲单位上班，对其寄予厚望，小张潜意识认为自己不能犯错不能承担错误。</p>', '', '2016年广东中山市教育系统事业单位教师招聘 《教育理论》题第27题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2026832';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2026832', '185098', '15', '<p>（1）请分析产生这种行为的原因</p>', '1', '<p>（1）从人本主义学习理论来看：</p><p>小张缺失爱与归属的需要。马斯洛把需要分为五个层次，他认为人类的需要是相互联系、相互依赖和彼此重叠的，它们排列成一个由低到高逐级上升的层次。其中，最低层次的需要是生理需要，接着是安全的需要，归属和爱的需要，尊重的需要，处在最高层次的是自我实现的需要。其中归属和爱的需要主要指需要朋友、爱人或孩子，渴望在团体中与同事有深厚的关系等。小张的父亲经常外出出差，母亲又上班，给予小张的爱太少，以至于小张缺少爱的需要，导致了对错误的不能容忍。</p><p>从学习动机来看：</p><p>小张属于附属内驱力，也就是外部动机。附属内驱力是学生为了保持家长和教师等的赞许或认可而努力学习的一种需要。它是一种外部的动机。小张认真学习是为了不辜负父母对其的厚望，也就是说是希望得到父母的认可，这是一种外部学习动机，不利于获得成功。</p><p>从气质类型来看：</p><p>小张属于抑郁性的气质类型。抑郁质气质类型的特点是行为孤僻，不善交往，易多愁善感，反应迟缓，适应能力差，容易疲劳，性格具有明显的内倾。小张不爱说话，不喜欢与人交流，也不爱发言，对于表扬不喜形于色，对于批评则大哭，说明她的气质类型是抑郁质。</p>', '5');
DELETE FROM edu_course_exercise_qa WHERE id='2026832120';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2026832120', '185098', '15', '<p>（2）如何进行心理教育？</p>', '1', '<p>（2）进行心理教育：</p><p>1.针对小张缺乏爱的需要，父母应该抽出更多的时间来陪伴她，给予她足够的爱。</p><p>2.针对小张的学习动机要激发小张的内部学习动机，比如学生的求知欲、学习兴趣、改善和提高自己能力的愿望等内部动机因素。</p><p>3.针对小张抑郁质的气质类型，应该更多地关心他，体贴他，鼓励他多参加活动，不要当众指责他。</p>', '10');
DELETE FROM edu_course_exercise_comment WHERE id='2026832';
