INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('204251835', ',204251835', '204251835', '<p>（2）概括总结题：在文中找到“开放的写作”所在的位置，联系前后文段，找到能解释这句话意思的句子，结合文段阐释对这句话的理解。</p>');
