INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('187920675', ',187920675', '187920596', '<p>（3）第一：根据问题查找知识点；第二：将题目涉及相关理论进行完善并完整论述；第三：结合实际进行分析。</p>');
