INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('188688', '15', '20202020', '<p>阅读关于“北方与南方的人文差异”的图文资料按要求完成教学设计任务。 </p><p>材料一 《义务教育地理课程标准（2011年版）》中的相关内容标准要求：“用事例说明四大地理单元自然地理环境对生产、生活的影响。”</p><p>材料二 某版本教科书中关于“北方与南方的人文差异”的内容。</p><p align="center">北方与南方的人文差异</p><p>受自然环境的影响，北方地区和南方地区的人们，在生产方式、生活习惯、文化传统等方面也有很大差异，正所谓“一方水土养一方人”。</p><p>比较图 5-12 和图  5-13 可以看出，北方村落和南方村落在自然景观和人文活动等方面，均有明显的差异。</p><p><img width="554px" height="340px" src="https://fb.fbstatic.cn/api/tarzan/images/15e27b49d838d91.png?width=700" /></p><p><img width="552px" height="347px" src="https://fb.fbstatic.cn/api/tarzan/images/15e27b4b1404d27.png?width=700" /></p><p>一一摘自某版本义务教育课程标准实验教科书（八年级下）</p>', '', '2015年上半年教师资格证考试《初中地理》题第31题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2085978';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2085978', '188688', '15', '<p>（1）写出本课的知识组成。</p>', '1', '<p>（1）本课的知识组成有：</p><p>①南方和北方地区的生产方式，包括土地利用类型、主要农作物和农作物熟制；</p><p>②南方和北方地区的交通特点；</p><p>③南方和北方地区的民居特点；</p><p>④南方和北方地区的体育活动特点；</p><p>⑤造成南北方差异的原因。</p>', '9');
DELETE FROM edu_course_exercise_qa WHERE id='208597860';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('208597860', '188688', '15', '<p>（2）根据课程标准和教材的知识组成，设计教学过程要点（应包括教师、学生的活动方式和活动内容）。</p>', '1', '<p>（2）设计教学过程</p><p><img width="402px" height="422px" src="https://fb.fbstatic.cn/api/tarzan/images/15e27b2c04ac090.png?width=700" /></p><p><img width="470px" height="258px" src="https://fb.fbstatic.cn/api/tarzan/images/15e3215a6f202a6.png?width=700" /></p><p><img width="462px" height="332px" src="https://fb.fbstatic.cn/api/tarzan/images/15e27b3df62708f.png?width=700" /></p>', '15');
