INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('20866060', ',20866060', '20866060', '<p>本题考查考生说课的设计能力及教学过程的分析能力。解答本题要求考生掌握常见的教学方法，比如讲授法、讨论法、直观演示法、读书指导法、参观教学法、现场教学法、自主学习法、合作探究法等。并且考生应该能够对具体教学过程进行分析，掌握说课的基本环节。</p>');
