INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2169693', ',2169693', '2169693', '<p>（1）重点语句的理解：寻找文段中高本汉所说的话，并通过寻找关键词的方式，对上下文进行概括、理解。</p>');
