INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2169693083', ',2169693083', '2169693083', '<p>（2）概括总结题：寻找文段中从哪几个方面来说明汉字结构特点的，联系前后文段进行阐释、分析。</p>');
