INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('194200', '15', '41414141', '<p>请认真阅读下列材料，并按要求作答。</p><p><img width="484px" height="626px" src="https://fb.fbstatic.cn/api/tarzan/images/162333085496ecd.png?width=700" /></p>', '', '2018年上半年教师资格证考试《教育教学知识与能力》（小学）题第27题', '1', '0');
