INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('194201', '15', '41414141', '<p>认真阅读下列材料，并按要求作答。</p><p><img width="432px" height="614px" src="https://fb.fbstatic.cn/api/tarzan/images/16233339b11b9d1.jpg?width=700" /></p>', '', '2018年上半年教师资格证考试《教育教学知识与能力》（小学）题第31题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2169770';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2169770', '194201', '15', '<p>（1）中国画有哪几大画科？中国画的表现形式有哪些？墨分五色，是哪五色？</p>', '1', '<p>（1）画科分类：人物画、山水画、花鸟画。</p><p>形式分类：工笔画、写意画。</p><p>墨分五色：焦浓重淡清。</p>', '10');
DELETE FROM edu_course_exercise_qa WHERE id='216977067';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('216977067', '194201', '15', '<p>（2）如指导中年级小学生学习，试拟定教学目标。</p>', '1', '<p>（2）教学目标：</p><p>知识与技能：尝试用中锋、侧锋、散锋等用笔方法生动表现动物的脸部特征与情趣。</p><p>过程与方法：通过引导学生观察分析，自主学习，学生能够掌握绘画步骤和用墨方法.</p><p>情感态度和价值观：通过学习能够增强对中国传统绘画的，热爱之情。形成人与动物、人与自然和谐相处的可持续发展意识。</p>', '10');
DELETE FROM edu_course_exercise_qa WHERE id='21697706784';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('21697706784', '194201', '15', '<p>（3）依据拟定的教学目标，设计“名作欣赏，技法学习”内容的新授教学活动并说明理由。</p>', '1', '<p>（3）新授教学。</p><p>环节一：直观感知。</p><p>1.同桌之间交流下你们喜欢的动物的脸部特征。</p><p>2.利用多媒体略赏花鸟画中动物题材作品，让学生初步感受画面内容氛围，观察动物面部的特点。</p><p>设计理念：先从学生喜欢的动物入手，不仅激发学生学习的兴趣，而且还关注了文化与生活。</p><p>环节二：分析示范。</p><p>首先，欣赏黄胄的《花猫》，研究表现方法。请学生在小组内讨论以下问题：</p><p>1.作品中的猫和图片中的猫有什么不同?</p><p>2.突出表现了花猫的哪些部分?对创作有什么好处?</p><p>3.画家主要运用了什么笔墨技法来表现猫?</p><p>小组讨论并汇报：作品中的花猫以中锋浓墨勾眼圈、点眼睛，白粉画胡须和眉毛;以湿笔浓淡结合的破魔法画身体，给人以体态丰硕的感觉;背部用浓墨破淡墨，勾画出花纹效果和绒毛质感;猫爪处浓淡相宜，以浓墨勾画出指甲的尖锐与锋利。教师对学生汇报讨论结果不完善的进行补充。</p><p>其次，画家黄胄抓住了猫的特点进行了生动的表现，美术小组讨论思考：怎样更好地抓住动物的特点？如何表现你喜欢的动物？</p><p>环节三：教师以小猫为例，利用所学技法进行创作示范：</p><p>1.脸型——先用几笔淡墨画出小猫的脸型。</p><p>2.五官——在脸的中间位置用淡墨中锋勾出眼睛的外形，在眼睛下方中间的位置侧锋画出鼻子和嘴，为了使眼睛更有神，再用重墨画眼珠。</p><p>3.毛——淡墨以皴擦法表现小猫脸部短而硬的毛。然后，笔锋蘸淡墨，笔尖蘸少量浓墨，以侧锋按压在一张废纸上，使笔尖水分变少并形成扁平的“散锋”，在小猫头顶及耳朵周围用毛笔的“散锋”适当添画短而稀疏的毛。</p><p>设计理念：此环节是本课重难点知识，所以进行了层层突破教学。让学生从精赏作品分析笔墨到谈论自己的创作想法最后教师示范技法，为接下来学生的创作设计了层层深入的学习过程，更好地攻克重难点。</p>', '20');
