INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('194204', '15', '41414141', '<p>请认真阅读下列材料，并按要求作答。<img width="553px" height="836px" src="https://fb.fbstatic.cn/api/tarzan/images/16233a92873e7ae.png?width=700" /></p>', '', '2018年上半年教师资格证考试《教育教学知识与能力》（小学）题第28题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2169779';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2169779', '194204', '15', '<p>（1）简述学生综合语言运用能力形成的基础。</p>', '1', '<p>（1）义务教育阶段英语课程的总目标：通过英语学习使学生形成初步的综合语言运用能力，促进心智发展，提高综合人文素养。综合语言运用能力的形成建立在语言技能、语言知识、情感态度、学习策略和文化意识等方面整体发展的基础之上。</p><p>其中，语言技能和语言知识是综合语言运用能力的基础。语言技能是语言运用能力的重要组成部分，主要包括听、说、读、写等方面的技能以及这些技能的综合运用。听和读是理解的技能，说和写是表达的技能，它们在语言学习和交际中相辅相成、相互促进。学生应通过大量的专项和综合性语言实践活动，形成综合语言运用能力，为真实的语言交际打下基础。因此，听、说、读、写既是学习的内容，又是学习的手段。语言技能标准以学生在某个级别“能做什么”为主要内容，这不仅有利于调动学生的学习积极性，促进学生语言运用能力的提高，也有利于科学、合理地评价学生的学习结果。学生在义务教育阶段应该学习和掌握的英语语言基础知识包括语音、词汇、语法以及用于表达常见话题和功能的语言形式等。语言知识是语言运用能力的重要组成部分，是发展语言技能的重要基础。</p>', '10');
DELETE FROM edu_course_exercise_qa WHERE id='21697795';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('21697795', '194204', '15', '<p>（2）如指导小学生学习，试拟定教学目标。</p>', '1', '<p>（2）Knowledge aims:</p><p>Students can understand the basic meaning of the listening materials.</p><p>Students can know Children’s Day is not the same day in different countries after listening.</p><p>Ability aim:</p><p>Students can improve their ability of listening to get the specific information by identifying key words while listening.</p><p>Emotional aims:</p><p>Students can learn different dates of Children’s Day in different countries and their different celebrating ways.</p><p>Students can broaden their horizon and know more about foreign cultures.Students can arise their interest in learning English.</p>', '10');
DELETE FROM edu_course_exercise_qa WHERE id='216977951';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('216977951', '194204', '15', '<p>（3）依据拟定的教学目标，设计导入和新授环节的教学活动并说明理由。</p>', '1', '<p>（3）Leading-in:</p><p>Play a video about Children’s Day.</p><p>Brainstorm:Ask students what activities they know that they usually do on Children’s day.</p><p>（设计理由：吸引学生的注意力，激发学生学习的兴趣，引入本节课话题，介绍话题背景，为后面的听力任务做好铺垫）</p><p>Listening:</p><p>Teacher plays the tape and asks students to find out the answer of the question“What day are they talking about?”to get the main idea.</p><p>Teacher plays the tape again and asks students to find out the answer of the question “How many countries are mentioned in the material?”to get specific information.</p><p>Teacher plays the tape one more time and asks students to do True or False exercise to enhance the details.</p><p>Students check answers in groups to see if they have different opinions and then teacher asks students to read the passage carefully to furtherly confirm their answers.</p><p>Teacher asks some students to give answers and corrects false ones.</p><p>（设计理由：听力共听三遍，第一遍回答问题，训练学生听取材料主题、了解大意的能力；第二遍回答关键信息问题，训练学生在听力过程中抓关键点的能力；第三遍完成判断对错练习，提高学生对细节问题的听力把握和能力。判断对错的练习较之前两个问题难度偏大，引导学生在听完后进行讨论，核对答案，让学生主动思考问题并带着问题进行听力材料的阅读，培养学生依据关键词查找信息的阅读能力。</p><p>在整个新授环节中，听力和阅读可谓层层递进、由浅入深，主要是为了培养学生把握大意、听取关键词和依据关键词查找信息的能力。）</p>', '20');
DELETE FROM edu_course_exercise_comment WHERE id='2169779';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2169779', ',2169779', '2169779', '<p>（1）义务教育阶段英语课程的总目标：通过英语学习使学生形成初步的综合语言运用能力，促进心智发展，提高综合人文素养。综合语言运用能力的形成建立在语言技能、语言知识、情感态度、学习策略和文化意识等方面整体发展的基础之上。</p><p>其中，语言技能和语言知识是综合语言运用能力的基础。语言技能是语言运用能力的重要组成部分，主要包括听、说、读、写等方面的技能以及这些技能的综合运用。听和读是理解的技能，说和写是表达的技能，它们在语言学习和交际中相辅相成、相互促进。学生应通过大量的专项和综合性语言实践活动，形成综合语言运用能力，为真实的语言交际打下基础。因此，听、说、读、写既是学习的内容，又是学习的手段。语言技能标准以学生在某个级别“能做什么”为主要内容，这不仅有利于调动学生的学习积极性，促进学生语言运用能力的提高，也有利于科学、合理地评价学生的学习结果。学生在义务教育阶段应该学习和掌握的英语语言基础知识包括语音、词汇、语法以及用于表达常见话题和功能的语言形式等。语言知识是语言运用能力的重要组成部分，是发展语言技能的重要基础。</p>');
