INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('194205', '15', '41414141', '<p>请认真阅读下述材料，并按要求作答。</p><p><img width="487px" height="334px" src="https://fb.fbstatic.cn/api/tarzan/images/16233a3fbc5a13d.png?width=700" /></p>', '', '2018年上半年教师资格证考试《教育教学知识与能力》（小学）题第29题', '1', '0');
