INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('197667', '15', '70707070', '<p>       某校某班，有一伙被老师称为“小捣蛋”的学生爱玩篮球，为了玩球和看球赛，常常影响学习，有时还迟到、旷课，甚至几次抢别班的球玩，引起纠纷，告到班主任那里。如何解决这个令人头疼的问题呢？班主任从中看到了一个可以引导的积极性。他组织这些“小捣蛋”成立了一个球队，选了队长，订了几条队规：一服从命令听指挥；二坚持练习守纪律；三不抢外班的球；四不做有损球队名誉的事。此后，每天开展练球，在活动中练出了纪律和自我控制能力。</p>', '', '2016年7月31日山东日照莒县教师招聘《公共学科知识+教育学、心理学相关知识》（职教教师）第53题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2222761';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2222761', '197667', '15', '<p>请运用教育原理进行分析。<br></p>', '1', '<p>这个案例体现了教师在实际教学过程中运用了因材施教原则、集体教育与个别教育相结合的原则、以及依靠积极因素，客服消极因素的原则、尊重学生与严格要求学生相结合原则。</p><p>①因材施教原则。因材施教原则是指进行德育要从学生品德发展的实际出发，根据他们的年龄特征和个性差异进行不同的教育，使每个学生的品德都能得到更好的发展。案例中小捣蛋们爱好篮球，因而王老师从打篮球出发，因势利导，用区别于其他同学的教学方式引导他们。</p><p>②集体教育与个别教育相结合。在德育过程中，教育者要善于组织和教育学生热爱集体，并依靠集体教育每个学生，同时通过对个别学生的教育，来促进集体的形成和发展，从而把集体教育和个别教育有机结合起来。案例中王老师通过指导他们组建篮球队，锻炼的他们的团队精神，恰好运用了集体对个人的影响，反过来队长的设立，也起到关键作用。</p><p>③依靠积极因素，克服消极因素。依靠积极因素，克服消极因素的原则是指在德育工作中，教育者要善于依靠、发扬学生自身的积极因素，调动学生自我教育的积极性，克服消极因素，实现品德发展内部矛盾的转化。案例中小捣蛋的积极因素是爱好运动，积极活泼开朗热情。但消极因素是无规矩、破坏性强、扰乱秩序。因此，王老师通过组建篮球队，既发扬了他们的积极因素，又克服了他们的消极因素。</p><p>④尊重学生与严格要求学生相结合。尊重学生与严格要求学生相结合的原则是指进行德育要把对学生的思想和行为的严格要求与对他们个人的尊重和信赖结合起来，使教育者对学生的影响与要求易于转化为学生的品德。案例中，王老师并没有对他们的打篮球行为嗤之以鼻，而是尊重他们的爱好，让他们组建篮球队。但是，必须做到严于律己，纪律严明，坚持训练，磨练意志。因此，最终练就了团队意识、合作精神、懂规矩、自我控制力好等品质。</p>', '8');
DELETE FROM edu_course_exercise_comment WHERE id='2222761';
