INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('197668', '15', '70707070', '<p>        王老师正在上课，忽然发现刘超正在聚精会神玩电子游戏机，为了不影响学生注意，又能制止刘超玩游戏机，王老师采取了适当的方法，保证了课堂有序进行。</p>', '', '2016年7月31日山东日照莒县教师招聘《公共学科知识+教育学、心理学相关知识》（职教教师）第54题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2222762';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2222762', '197668', '15', '<p>请问王老师采用的何种方法？（至少举出2种方法）<br></p>', '1', '<p>方法一：老师运用非言语的手段。王老师可以选择适当的时候通过慢慢走近刘超来制止上课玩游戏。</p><p>方法二：王老师可以创设一个互动情境让刘超参与进来，让刘超给大家讲讲游戏中的故事，大家回家后写自己最喜爱的一种活动，把过程讲清楚。既纠正其问题行为，并维护课堂的有序进行。</p>', '4');
DELETE FROM edu_course_exercise_qa WHERE id='2222763';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2222763', '197668', '15', '<p>你认为应如何正确对待学生的课堂行为？</p>', '1', '<p>（1）运用积极的言语和非言语手段调控</p><p>（2）合理运用惩罚</p><p>（3）引导学生参与学习活动，不留给学生违纪的时间</p><p>（4）进行心理辅导</p>', '2.05');
