INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2224023', ',2224023', '2224023', '<p>（1）第一：根据问题回归材料；第二：将题目中涉及相关理论进行完善并且完整论述；第三：总结观点。</p>');
