INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('197684', '15', '70707070', '<p>阅读材料，回等问题。</p><p>       物理学大师费曼说：“我讲授的目的不是为你们参加考试，我最喜欢做的是展示这个奇妙世界的一部分供你们欣赏……”</p><p>       在教育孩子的过程中，我国一些家长和教师认为，任何兴趣都不是凭空而来的，当孩子有了一定成就，没准就会产生兴趣。所以，这些家长才会逼迫孩子上各种补习班；教师才会不停地给学生灌输知识，罚学生抄很多遍作业等。</p>', '', '2018年7月7日年安徽省教师招聘考试《教育综合知识》（小学）题第57题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2224059';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2224059', '197684', '15', '<p>（1）请你谈谈兴趣对学习的作用。</p>', '1', '<p>（1）兴趣是人们对事物的一种认识倾向，伴随着积极的情绪体验，特别是对个体的认知活动有巨大的推动作用。结合材料，兴趣对学习的作用包括：</p><p>① 对将要从事的活动起着推动作用，即兴趣能变“厌学”为“乐学”。夸美纽斯说：“课堂应是快乐的场所。”兴趣与积极、愉快的情感相伴随，有了它学生视学习为乐事，自觉“心中喜悦”，充满愉快。学生想干的事才能干得好。</p><p>② 对正在进行的活动具有推动作用，即兴趣能变学习的“低效率”为“高效率”。无兴趣的学习，脑细胞呈封闭状态，排斥外界信息，表现为不想学，一学习就感到头痛，用脑效率自然就低。有兴趣的学习，脑细胞活跃，信息接收快，脑力劳动效率高。</p><p>③ 对学生的创造性活动具有促进作用，即兴趣能变“被动学习”为“主动学习”。兴趣产生巨大的内驱力，是学习中的巨大推动力。培养兴趣可以充分发挥意识的能动性，甚至创造性地解决学习过程中遇到的各种困难和问题。</p><p>④ 对未来生活和发展具有准备作用。兴趣的培养和形成，直接关系到学生学习的志趣，志向确立和个性发展，对将来的事业有重大的价值。</p>', '5');
DELETE FROM edu_course_exercise_qa WHERE id='22240598';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('22240598', '197684', '15', '<p>（2）如何培训小学生的学习兴趣？</p>', '1', '<p>（2）培训小学生的学习兴趣包括：</p><p>① 通过各种活动发展学生的兴趣；</p><p>② 通过提高教学水平，引发学生兴趣；</p><p>③ 引导学生将广阔兴趣与中心兴趣结合起来；</p><p>④ 要根据学生的年龄特征来提高学生的学习兴趣；</p><p>⑤ 要根据学生的知识基础培养学生的学习兴趣；</p><p>⑥ 通过积极的评价，使学生的兴趣得以强化；</p><p>⑦ 充分利用原有兴趣的迁移。</p>', '10');
