INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2226770', ',2226770', '2226770', '<p>第一：根据问题回归材料；第二：判断哪位老师的教学目标较为理想；第三：结合材料进行分析。</p>');
