INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('198454', '15', '40404040', '<p><img width="554px" height="216px" src="https://fb.fbstatic.cn/api/tarzan/images/1659388478b4588.png?width=700" /></p>', '', '2017年上半年教师资格证考试《初中生物》真题第29题', '1', '0');
