INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('198457', '15', '40404040', '<p align="center">“藻类植物”教学片段</p><p>1.展示“海带排骨汤”紫菜蛋汤”等食物图片，提问学生这些食物含有哪类植物、引出学习课题。</p><p>教师提问：“我们还知道生活中有哪些藻类植物?”学生面面相觑，在教师启发下，才想到“螺旋藻”，教师因势利导，提出。“自然界中有各种各样的藻类植物，我们的校园中就有很多。.</p><p>2.教师指着教学楼旁水体发绿的小池塘提问：“小池塘水为什么会发绿呢?”学生答：“可能有藻类植物。”教师接着说：“如何能证明你们的推测是对的呢?”学生回答：“可以采集水样回实验室用显微镜观察。”学生从教师事先采集的小池塘水样中观察到了螺旋藻、微囊藻等藻类植物。</p><p>3.教师又展示了预先准备好的、从住宅区旁的小河沟中采集的绿色丝状藻类，问：“这是什么?”学生齐声回答：“水绵。”“真的是水绵吗?”学生在显微镜下观察该植物的形态特征，结果大大出乎所料，该藻类植物的叶绿体居然是球状的!原来，这是在外形上跟水绵非常相似的刚毛藻。</p><p>4.在教师的引导下，学生结合自己的观察和经验，总结了藻类植物的一般特征和藻类与人类的关系。之后，教师布置了如下作业:课后通过网络或报刊查询相关资料，了解2007年太湖蓝藻暴发事件，并与父母讨论该如何保护水资源和爱护环境。</p>', '', '2016年下半年教师资格证考试《初中生物》真题第18题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='224432630';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('224432630', '198457', '15', '<p>（2）分析该教学过程体现的科学本质教育的内容。</p>', '1', '<p>（2）①关注科学知识——在教学中凸显概念教学。材料中，教师通过图片的呈现，引导学生发现并观察生活中的不同藻类，以及利用网络了解不同的藻类，逐步让学生建立起藻类的概念。</p><p>②学习科学方法——在教学中彰显探究理念。材料中，教师通过图片引出课题，激发学生的学习欲望；又通过问题自然而然地引导学生自己去探索观察学校和生活中的藻类，通过自己积极主动的探索，掌握知识；最后要求学生自主地去网络上学习相关知识。整个教学过程中都体现了探究理念。</p>', '10');
DELETE FROM edu_course_exercise_comment WHERE id='224432630';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('224432630', ',224432630', '224432630', '<p>（2）①关注科学知识——在教学中凸显概念教学。材料中，教师通过图片的呈现，引导学生发现并观察生活中的不同藻类，以及利用网络了解不同的藻类，逐步让学生建立起藻类的概念。</p><p>②学习科学方法——在教学中彰显探究理念。材料中，教师通过图片引出课题，激发学生的学习欲望；又通过问题自然而然地引导学生自己去探索观察学校和生活中的藻类，通过自己积极主动的探索，掌握知识；最后要求学生自主地去网络上学习相关知识。整个教学过程中都体现了探究理念。</p>');
