INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, dw_checked, is_pk) VALUES ('198458', '15', '40404040', '<p>       某版义务教育生物学教材“想一想，议一议”栏目内容如下:</p><p><img width="554px" height="443px" src="https://fb.fbstatic.cn/api/tarzan/images/16593e58f7be152.png?width=700" /></p>', '', '2016年下半年教师资格证考试《初中生物》真题第19题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2244327054';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2244327054', '198458', '15', '<p>（2）分析教学中使用该栏目应注意的事项。</p>', '1', '<p>（2）①应当根据实际情况替换“想一想，议一议”的例子。教材的安排是固定的，尽管教材“想一想，议一议”所提供的事例是编写者反复讨论并保留下来的，但仍然不能照顾到所有地区和学生，所以教学中教师要结合学生的生活实际和自身体验对这部分内容进行替换，用学生熟悉的例子和情境进行教学，不仅可以丰富教学内容，而且可以提高学生的学习兴趣，也更易于学生对相关内容的理解和记忆。</p><p>②要适当地拓展“想一想，议一议”的内容。新课程提倡教材内容要广泛，教师在教学过程中不仅仅要注重教材所阐述的事例，更要注重结合学生的生活哦经验积累，将生活中鲜活的、与该事例相关的、相似的事例收集整合在一起进行分析，如长得相像的双胞胎、父与子等事例，不仅可以强化和丰富学生的感受，还可以拓宽视野，有效地提高学生学习的积极性，激发学生浓厚的学习兴趣。</p><p>③可改变“想一想，议一议”的呈现方式。让学生读“想一想，议一议”，绝没有一段视频使学生认识更为深刻。虽然文字形象有趣，可激发学生丰富的想象力，但是在教学中教师若利用多媒体把文字描述改变为一段视频，这样变静态为动态，化无声为有声，融知识教学于情趣中，可以提高学生的学习兴趣，激发求知欲，调动学习积极性，这样就克服了传统教材内容静态、呆板的缺陷，使课程更生动，学生学得更轻松。</p>', '10');
DELETE FROM edu_course_exercise_comment WHERE id='2244327054';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2244327054', ',2244327054', '2244327054', '<p>（2）①应当根据实际情况替换“想一想，议一议”的例子。教材的安排是固定的，尽管教材“想一想，议一议”所提供的事例是编写者反复讨论并保留下来的，但仍然不能照顾到所有地区和学生，所以教学中教师要结合学生的生活实际和自身体验对这部分内容进行替换，用学生熟悉的例子和情境进行教学，不仅可以丰富教学内容，而且可以提高学生的学习兴趣，也更易于学生对相关内容的理解和记忆。</p><p>②要适当地拓展“想一想，议一议”的内容。新课程提倡教材内容要广泛，教师在教学过程中不仅仅要注重教材所阐述的事例，更要注重结合学生的生活哦经验积累，将生活中鲜活的、与该事例相关的、相似的事例收集整合在一起进行分析，如长得相像的双胞胎、父与子等事例，不仅可以强化和丰富学生的感受，还可以拓宽视野，有效地提高学生学习的积极性，激发学生浓厚的学习兴趣。</p><p>③可改变“想一想，议一议”的呈现方式。让学生读“想一想，议一议”，绝没有一段视频使学生认识更为深刻。虽然文字形象有趣，可激发学生丰富的想象力，但是在教学中教师若利用多媒体把文字描述改变为一段视频，这样变静态为动态，化无声为有声，融知识教学于情趣中，可以提高学生的学习兴趣，激发求知欲，调动学习积极性，这样就克服了传统教材内容静态、呆板的缺陷，使课程更生动，学生学得更轻松。</p>');
