INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2026824', '12', '70707070', '<p>贯彻启发性原则的基本要求？</p>', '', '2016年广东中山市教育系统事业单位教师招聘 《教育理论》题第23题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2026824';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2026824', '2026824', '12', '', '1', '<p>启发性教学原则是指教师在教学工作中依据学习过程的客观规律，运用各种教学手段充分调动学生学习主动性、积极性，引导他们独立思考，积极探索，生动活泼地学习，自觉地掌握科学知识和提高分析问题和解决问题的能力。</p><p>贯彻此原则的要求</p><p>1.加强学习的目的性教育，调动学生学习的主动性。</p><p>2.启发学生独立思考，发展学生的逻辑思维能力。</p><p>3.让学生动手实践，培养学生独立解决问题的能力。</p><p>4.发扬教学民主。包括：建立民主平等的师生关系，创造民主和谐的教学气氛，鼓励学生发表不同的见解，允许学生向教师提问、质疑等。</p>', '5.0');
DELETE FROM edu_course_exercise_comment WHERE id='2026824';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2026824', ',2026824', '2026824', '<p>启发性教学原则是指教师在教学工作中依据学习过程的客观规律，运用各种教学手段充分调动学生学习主动性、积极性，引导他们独立思考，积极探索，生动活泼地学习，自觉地掌握科学知识和提高分析问题和解决问题的能力。</p><p>贯彻此原则的要求</p><p>1.加强学习的目的性教育，调动学生学习的主动性。</p><p>2.启发学生独立思考，发展学生的逻辑思维能力。</p><p>3.让学生动手实践，培养学生独立解决问题的能力。</p><p>4.发扬教学民主。包括：建立民主平等的师生关系，创造民主和谐的教学气氛，鼓励学生发表不同的见解，允许学生向教师提问、质疑等。</p>');
