INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2056250', '2056250', '12', '', '1', '<p>课堂教学中运用讨论法的基本要求有三点：</p><p>（1）讨论前做好充分准备。</p><p>（2）讨论中要对学生进行启发诱导。</p><p>（3）在讨论结束时要做好小结。</p>', '0');
