INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2056254', '12', '70707070', '<p>简述现阶段我国教育目的的基本精神。</p>', '', '2017年安徽省教师招聘考试《教育综合知识》（小学）题第42题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2056254';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2056254', '2056254', '12', '', '1', '<p>现阶段我国教育目的的基本精神包括以下三点：</p><p>（1）我们要求培养的人是社会主义建设者和接班人，因此要坚持思想政治道德素质与科学文化知识能力的统一。</p><p>（2）我们要求学生在德、智、体等方面全面发展，要求坚持脑力劳动与体力劳动两方面的和谐发展。</p><p>（3）适应时代发展的要求，强调学生个性的发展，培养学生的创造精神和实践能力。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2056254';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2056254', ',2056254', '2056254', '<p>现阶段我国教育目的的基本精神包括以下三点：</p><p>（1）我们要求培养的人是社会主义建设者和接班人，因此要坚持思想政治道德素质与科学文化知识能力的统一。</p><p>（2）我们要求学生在德、智、体等方面全面发展，要求坚持脑力劳动与体力劳动两方面的和谐发展。</p><p>（3）适应时代发展的要求，强调学生个性的发展，培养学生的创造精神和实践能力。</p>');
