INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2056308', '2056308', '12', '', '1', '<p>读书指导法是教师指导学生通过阅读教科书和参考书，培养学生自学能力的一种方法。其运用要求包括：</p><p>（1）提出明确的目的、要求和思考题。</p><p>（2）教给学生读书的方法。</p><p>（3）加强评价和辅导。</p><p>（4）适当组织学生交流读书心得。</p>', '0');
