INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2057584', '12', '70707070', '<p>加涅的信息加工学习理论提出的学习过程包括哪八个阶段？</p>', '', '2015年12月天津市实验中学滨海学校《教育综合知识》第37题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2057584';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2057584', '2057584', '12', '', '1', '<p>加涅根据信息加工理论提出了学习过程的基本模式，认为学习过程就是一个信息加工的过程，即学习者将来自环境刺激的信息进行内在的认知加工的过程。根据信息加工的流程，加涅进一步认为，学习包括外部条件和内部条件，学习过程实际上就是学习者头脑中的内部活动。他把学习过程划分为八个阶段：</p><p>①动机阶段——期望 1/2 激发动机</p><p>②领会阶段——注意：选择性知觉 1/2 告知目标</p><p>③习得阶段——编码：储存登记 2/3 指导注意</p><p>④保持阶段——记忆贮存 3/4 刺激回忆</p><p>⑤回忆阶段——提取 3/4 提供学习指导</p><p>⑥概括阶段——迁移 4/5 增强保持</p><p>⑦作业阶段——反应 6 促进学习迁移</p><p>⑧反馈阶段——强化 7/8 布置作业提供反馈</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2057584';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2057584', ',2057584', '2057584', '<p>加涅根据信息加工理论提出了学习过程的基本模式，认为学习过程就是一个信息加工的过程，即学习者将来自环境刺激的信息进行内在的认知加工的过程。根据信息加工的流程，加涅进一步认为，学习包括外部条件和内部条件，学习过程实际上就是学习者头脑中的内部活动。他把学习过程划分为八个阶段：</p><p>①动机阶段——期望 1/2 激发动机</p><p>②领会阶段——注意：选择性知觉 1/2 告知目标</p><p>③习得阶段——编码：储存登记 2/3 指导注意</p><p>④保持阶段——记忆贮存 3/4 刺激回忆</p><p>⑤回忆阶段——提取 3/4 提供学习指导</p><p>⑥概括阶段——迁移 4/5 增强保持</p><p>⑦作业阶段——反应 6 促进学习迁移</p><p>⑧反馈阶段——强化 7/8 布置作业提供反馈</p>');
