INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2058532', '12', '70707070', '<p>教学中如何贯彻启发性教学原则。</p>', '', '2016年1月河南省周口联营中专教师招聘 《教育基础知识》第54题', '1', '0');
