INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2058532', '2058532', '12', '', '1', '<p>教学中贯彻启发性教学原则的要求如下：</p><p>（1）加强学习的目的性教育，调动学生学习的主动性。</p><p>（2）启发学生独立思考，发展学生的逻辑思维能力。</p><p>（3）让学生动手实践，培养学生独立解决问题的能力。</p><p>（4）发扬教学民主，包括：建立民主平等的师生关系，创造民主和谐的教学气氛，鼓励学生发表不同的见解，允许学生向教师提问、质疑等。</p>', '0');
