INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2059372', '2059372', '12', '', '1', '<p>（1）备课——上课前的准备工作，是教好课的前提。</p><p>（2）上课——教学工作的中心环节。</p><p>（3）课外作业的布置与批改。</p><p>（4）课外辅导——上课必要的补充。</p><p>（5）学生学业成绩的评价。</p>', '0');
