INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2059652', '12', '70707070', '<p>讲授法</p>', '', '2016年4月25日天津滨海高新区管委会教师招聘《教师综合知识测试》第31题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2059652';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2059652', '2059652', '12', '', '1', '<p>讲授法</p><p>（1）定义：讲授法是教师运用口头语言系统地向学生传授知识的一种方法。</p><p>（2）讲授法的方式主要有讲述、讲解、讲读、讲演四种方式。</p><p>（3）讲授法的优缺点：能在短时间内系统传授知识，但不利于学生主动性的发挥。</p><p>（4）运用的基本要求</p><p>第一，讲授内容要有科学性、系统性、思想性。既要突出重点、难点，又要系统、</p><p>全面；既要使学生获得可靠知识，又要使学生在思想上有所提高。</p><p>第二，注意启发。在讲授中善于诘问并引导学生分析和思考问题，使学生的认识活</p><p>动积极开展，从而使学生自觉地领悟知识。</p><p>第三，讲究语言艺术。力图语言清晰、准确、简练、形象、条理清楚、通俗易懂，</p><p>讲授的音量、速度要适度，注意音调的抑扬顿挫；以姿势助说话，提高语言的感染力。</p><p>第四，要组织学生听讲。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2059652';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2059652', ',2059652', '2059652', '<p>讲授法</p><p>（1）定义：讲授法是教师运用口头语言系统地向学生传授知识的一种方法。</p><p>（2）讲授法的方式主要有讲述、讲解、讲读、讲演四种方式。</p><p>（3）讲授法的优缺点：能在短时间内系统传授知识，但不利于学生主动性的发挥。</p><p>（4）运用的基本要求</p><p>第一，讲授内容要有科学性、系统性、思想性。既要突出重点、难点，又要系统、</p><p>全面；既要使学生获得可靠知识，又要使学生在思想上有所提高。</p><p>第二，注意启发。在讲授中善于诘问并引导学生分析和思考问题，使学生的认识活</p><p>动积极开展，从而使学生自觉地领悟知识。</p><p>第三，讲究语言艺术。力图语言清晰、准确、简练、形象、条理清楚、通俗易懂，</p><p>讲授的音量、速度要适度，注意音调的抑扬顿挫；以姿势助说话，提高语言的感染力。</p><p>第四，要组织学生听讲。</p>');
