INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2059890', '12', '70707070', '<p>请简述一堂课的基本组成部分。</p>', '', '2016年1月江苏省徐州铜山区教师招聘考试题（精编）第38题', '1', '0');
