INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2059982', '12', '70707070', '<p>联系实际情况谈谈一个合格教师的职业素质有哪些？</p>', '', '2016年5月江苏南通市崇川区教育系统招聘《专业知识（教育理论）》(精编）第41题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2059982';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2059982', '2059982', '12', '', '1', '<p>（一）道德素养</p><p>1. 对待事业：忠诚于人民的教育事业</p><p>2. 对待学生：热爱学生</p><p>3. 对待集体：具有团结协作的精神</p><p>4. 对待自己：以身作则，为人师表</p><p>（二）知识素养</p><p>1. 政治理论修养</p><p>2. 精深的学科专业知识</p><p>3. 广博的科学文化知识</p><p>4. 必备的教育科学知识</p><p>5. 先进的教育理念</p><p>（三）能力素养</p><p>1. 语言表达能力</p><p>2. 教育教学能力</p><p>3. 组织管理能力</p><p>4. 自我调控和自我反思能力（较高的教育机智）</p><p>此外，教师还应该具备教育科研能力、学习能力、观察学生的能力、创新能力以及运用现代教育技术手段的能力。</p><p>（四）职业心理素养</p><p>1. 高尚的师德</p><p>2. 愉悦的情感</p><p>3. 良好的人际关系</p><p>4. 健康的人格</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2059982';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2059982', ',2059982', '2059982', '<p>（一）道德素养</p><p>1. 对待事业：忠诚于人民的教育事业</p><p>2. 对待学生：热爱学生</p><p>3. 对待集体：具有团结协作的精神</p><p>4. 对待自己：以身作则，为人师表</p><p>（二）知识素养</p><p>1. 政治理论修养</p><p>2. 精深的学科专业知识</p><p>3. 广博的科学文化知识</p><p>4. 必备的教育科学知识</p><p>5. 先进的教育理念</p><p>（三）能力素养</p><p>1. 语言表达能力</p><p>2. 教育教学能力</p><p>3. 组织管理能力</p><p>4. 自我调控和自我反思能力（较高的教育机智）</p><p>此外，教师还应该具备教育科研能力、学习能力、观察学生的能力、创新能力以及运用现代教育技术手段的能力。</p><p>（四）职业心理素养</p><p>1. 高尚的师德</p><p>2. 愉悦的情感</p><p>3. 良好的人际关系</p><p>4. 健康的人格</p>');
