INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2060278', '12', '70707070', '<p>《中小学教师职业道德规范》中，对教师为人师表方面做出了哪些要求。</p>', '', '2014年湖南衡阳市南岳区教师招聘考试（教育理论）（精编）第63题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2060278';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2060278', '2060278', '12', '', '1', '<p>为人师表的要求包括：</p><p>（1）语言规范健康</p><p>①教师的语言要纯洁、文明、健康。</p><p>②教师的语言要准确、鲜明、简练。</p><p>③教师的语言要热情、诚恳，富于激励性。</p><p>④教师的语言要含蓄、幽默，富于启发性。</p><p>（2）教态高雅自如</p><p>①教师的站态，既要端庄稳重，又要富有活力。</p><p>②教师的手势要简洁、适度、准确、有力。</p><p>③教师的表情要丰富、自然、亲切。</p><p>④教师的坐态应保持上身相对直立，双腿自然并放，给人以端庄和谐之感。</p><p>（3）仪表朴实大方</p><p>①教师的仪表应具有美感（基本要求）。</p><p>②教师的仪表应具有职业感。</p><p>③仪表应适合自己的性格特点。</p><p>④教师的仪表还应具有时代感。</p><p>（4）身教重于言教</p><p>①言行一致，表里如一。</p><p>②以身作则，起榜样示范作用。</p><p>③坚持“身教重于言教”。正如叶圣陶所说：身教最可贵，行知不可分。”</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2060278';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2060278', ',2060278', '2060278', '<p>为人师表的要求包括：</p><p>（1）语言规范健康</p><p>①教师的语言要纯洁、文明、健康。</p><p>②教师的语言要准确、鲜明、简练。</p><p>③教师的语言要热情、诚恳，富于激励性。</p><p>④教师的语言要含蓄、幽默，富于启发性。</p><p>（2）教态高雅自如</p><p>①教师的站态，既要端庄稳重，又要富有活力。</p><p>②教师的手势要简洁、适度、准确、有力。</p><p>③教师的表情要丰富、自然、亲切。</p><p>④教师的坐态应保持上身相对直立，双腿自然并放，给人以端庄和谐之感。</p><p>（3）仪表朴实大方</p><p>①教师的仪表应具有美感（基本要求）。</p><p>②教师的仪表应具有职业感。</p><p>③仪表应适合自己的性格特点。</p><p>④教师的仪表还应具有时代感。</p><p>（4）身教重于言教</p><p>①言行一致，表里如一。</p><p>②以身作则，起榜样示范作用。</p><p>③坚持“身教重于言教”。正如叶圣陶所说：身教最可贵，行知不可分。”</p>');
