INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2068768', '12', '70707070', '<p>简述学习策略的特征。</p>', '', '2013年山东省淄博市博山区教师招聘考试题第44题', '1', '0');
