INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2069530', '2069530', '12', '', '1', '<p>维持课堂纪律有以下四个策略：</p><p>（1）建立有效的课堂规则。</p><p>（2）合理组织课堂教学。</p><p>（3）做好课堂监控。</p><p>（4）培养学生的自律品质。</p>', '0');
