INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2071982', '12', '70707070', '<p>试述良好师生关系建构的基本策略。</p>', '', '2013年山东省淄博市淄川区教师招聘考试题第13题', '1', '0');
