INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2074498', '12', '70707070', '<p>教师的职业角色有哪些?</p>', '', '2014年贵州省黔东南镇远县教师招聘考试题第48题', '1', '0');
