INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2074498', '2074498', '12', '', '1', '<p>教师职业的最大特点在于职业角色的多样化，教师的职业角色包括以下几点：</p><p>（1）传道者的角色；</p><p>（2）授业解惑者的角色；</p><p>（3）示范者的角色（榜样角色）；</p><p>（4）教育教学活动的设计者、组织者和管理者；</p><p>（5）朋友的角色；</p><p>（6）研究者的角色。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2074498';
INSERT INTO edu_course_exercise_comment (id, path, course_exercise_qa_id, comment) VALUES ('2074498', ',2074498', '2074498', '<p>教师职业的最大特点在于职业角色的多样化，教师的职业角色包括以下几点：</p><p>（1）传道者的角色；</p><p>（2）授业解惑者的角色；</p><p>（3）示范者的角色（榜样角色）；</p><p>（4）教育教学活动的设计者、组织者和管理者；</p><p>（5）朋友的角色；</p><p>（6）研究者的角色。</p>');
