INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2075758', '12', '70707070', '<p>党的十八大倡导的社会主义核心价值观的基本内容有哪些?</p>', '', '2015年湖南省长沙市岳麓区教师招聘考试题第37题', '1', '0');
