INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2076820', '12', '70707070', '<p>简述备课的基本步骤。</p>', '', '2014年浙江省教师招聘考试题 《教育基础知识•中学》第20题', '1', '0');
