INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2087164', '12', '60606060', '<p>《义务教育美术课程标准(2011年版)》在“评价建议”中提出，“采用多种评价方式评价美术作业”。其中，“多种”指的是什么?</p>', '', '2013年下半年教师资格证考试《初中美术》题第30题', '1', '0');
